    // console.log(val2,"val2")
    return (
        <div className='search-container'>
            <div className='searchIcon'>
                <Icon.Search/>
            </div>
            <Form.Control type="search" placeholder='Search by name, mobile or email' value={filter || ''}  onChange={handleChange} />
        </div>
    )
}
export default GlobalFilter;
