import React , {useMemo, useEffect} from 'react';
import { useTable, usePagination, useGlobalFilter, useFilters, useRowSelect } from 'react-table';
import { enrolmentStatusFilter, profileStatusFilter, qcStatusFilter, bankAccountStatusFilter, supervisorStatusFilter } from '../component/Filter';
import ModalDialog from '../component/ModalDialog';
import Header from '../component/Header';
import * as Icon from 'react-bootstrap-icons';
import Form from 'react-bootstrap/Form';
import Select from '../component/SelectComponent';
import {format, isValid} from 'date-fns';
import APIDataColumns from '../APIDataColumns.json'

function PartnerManagementDashboard1() {
  const [showData,  setShowData] = React.useState('turtlemint');

  const showDataIfTM = (e) => {
    setShowData(e.target.value)
  }

  const data = useMemo(() => showData === 'turtlemint' ? APIDataColumns : [] , [showData]);
  const initiallySelectedRows = useMemo(() => new Set(["1"]), []);

  const IndeterminateCheckbox = React.forwardRef(
    ({ indeterminate, ...rest }, ref) => {
      const defaultRef = React.useRef()
      const resolvedRef = ref || defaultRef

      React.useEffect(() => {
        resolvedRef.current.indeterminate = indeterminate
      }, [resolvedRef, indeterminate])

      return (
        <div>
          <Form>
            <Form.Check
              type="checkbox" 
              ref={resolvedRef} 
              {...rest} 
            />
          </Form>
        </div>
      )
    }
  )

  const columns = React.useMemo(() => [
    {
      Header: 'DP Number',
      id: "dpNo",
      accessor: `dpNo`,
      disableGlobalFilter: false,
      Cell: ({ row }) => {
        return (
         <div>DP - {row.values.dpNo}</div>
        );
      },      
    },
    {
      Header: 'Full Name',
      id: "name",
      accessor: 'name',
      disableGlobalFilter: false,
    },
    {
      Header: 'Sales Excecutive',
      id: "supervisorName",
      accessor: row => (row.supervisor !== null && row.supervisor.name !== null && row.supervisor.name ) ? true : false,
      Filter: supervisorStatusFilter,   
      Cell: ({ row }) => {
        return (
          <span>
          {(row.original.supervisor !== undefined && row.original.supervisor !== null && row.original.supervisor.name !== null) ? (
            <span>{row.original.supervisor.name}</span>
          ) : (
            <span></span>
          )}
        </span>
        );
      },   
    },
    {
      Header: 'To be Verified Date',
      id: "toBeVerifiedDate",
      accessor: 'toBeVerifiedDate',
      disableGlobalFilter: true,
      Cell: ({ row }) => {
        return (
          <div>
            {row.original.toBeVerifiedDate !== null ? (
              <div>{isValid(new Date(row.original.toBeVerifiedDate)) ? format(row.original.toBeVerifiedDate,"dd MMM yyyy hh:mm"): null}</div>
            ): null}
          </div>
        );
      },    
    },
    {
      Header: 'Verification Date',
      id: "verificationDate",
      accessor: 'verificationDate',
      disableGlobalFilter: true,
      Cell: ({ row }) => {
        return (
          <div>
            {row.original.verificationDate !== null ? (
              <div>{isValid(new Date(row.original.verificationDate)) ? format(row.original.verificationDate,"dd MMM yyyy hh:mm"): null}</div>
            ): null}
          </div>        );
      }, 
    },
    {
      Header: 'Profile Status',
      id: "status",
      accessor: "status",
      disableGlobalFilter: true,
      filter: "equals",
      Filter: profileStatusFilter,
      Cell: ({ row }) => {
        return (
         <div>{row.original.status === 'verified' ? 'Verified' : row.original.status === 'not_verified' ? 'Not Verified' : row.original.status === 'to_verify' ? 'To Verify' : row.original.status === 'rejected' ? 'Rejected' : ''}</div>
        );
      },    
    },
    {
      Header: 'Enrollemnt Status',
      id: "eligibleForLMS",
      // accessor: "eligibleForLMS",
      accessor: row => (row.eligibleForLMS !== null && row.eligibleForLMS) ? true : false,
      Filter: enrolmentStatusFilter,
      disableGlobalFilter: true,
      Cell: ({ row }) => {
        return (
          <div>
            {row.original.eligibleForLMS ? 'Eligible' : 'Not Eligible'}
          </div>
        );
      },    
    },
    {
      Header: 'QC Status',
      filter: "equals",
      Filter: qcStatusFilter,
      disableGlobalFilter: true,
      accessor: "partnerQc.qcStatus",
      Cell: ({ row }) => {
        return (
          <span>
            {(row.original.partnerQc !== undefined && row.original.partnerQc !== null && row.original.partnerQc.qcStatus !== null) ? (
              <span>{row.original.partnerQc.qcStatus === 'qc_verified' ? 'QC Verified' : row.original.partnerQc.qcStatus === 'qc_rejected' ? 'QC Rejected' : '-'}</span>
            ) : (
              <span>-</span>
            )}
          </span>
        ); 
      },
    },
    {
      Header: 'Verification Owners',
      accessor: 'verificationOwner.name',
      disableGlobalFilter: true,
      Cell: ({ row }) => {
        return (
          <span>
              <span>
                {(row.original.verificationOwner !== undefined && row.original.verificationOwner !== null && row.original.verificationOwner.name !== null ) ? (
                  <span>{row.original.verificationOwner.name}</span>
                ) : (
                  <span>-</span>
                )}
              </span>
          </span>
        ); 
      },
    },
    {
      Header: 'Bank Account Status',
      filter: "weakEquals",
      Filter: bankAccountStatusFilter,
      disableGlobalFilter: true,
      accessor: "bankDetails.verificationMode",
      Cell: ({ row }) => {
        return (
          <span>
            {(row.original.bankDetails !== undefined && row.original.bankDetails !== null && row.original.bankDetails.verificationMode !== null ) ? (
              <span>{row.original.bankDetails.verificationMode === 'signzy' ? 'Signzy Verified' : row.original.bankDetails.verificationMode === 'cashfree' ? 'Cashfree Verified' : row.original.bankDetails.verificationMode === 'manual' ? 'Manually Verified' : '-'}</span>
            ) : (
              <span>-</span>
            )}
          </span>
        ); 
      },
    },
    {
      Header: 'Mobile',
      id: "mobile",
      accessor: 'mobile',
      disableGlobalFilter: false
    },
    {
      Header: 'EmailId',
      id: "email",
      accessor: 'email',
      disableGlobalFilter: false
    }
  ], []);

  const defaultColumn = React.useMemo(
    () => ({
      // Let's set up our default Filter UI
      Filter: ""
    }),
    []
  );

  //tenant data
  const tenant = [
    {
      displayName: 'TURTLEMINT',
      value: 'turtlemint'
    },
    {
      displayName: 'AIRTEL',
      value: 'airtel'
    },
    {
      displayName: 'TURTLEPOSP',
      value: 'turtleposp'
    }
  ]

  const { getTableProps, 
    getTableBodyProps, 
    headerGroups, 
    page, 
    nextPage,
    previousPage,
    canNextPage,
    canPreviousPage,
    pageOptions,
    state,
    gotoPage,
    setPageSize,
    setGlobalFilter,
    prepareRow 
  } = useTable({columns, data, defaultColumn, initialState: { filters: [
    {
      id: 'status',
      value: 'verified',
    }
  ], selectedRowPaths: initiallySelectedRows, pageSize: 2, hiddenColumns : ["mobile", "email"] },
    autoResetSelectedRows: false,
    autoResetSelectedCell: false,
    autoResetSelectedColumn: false,}, 
    useGlobalFilter, useFilters, usePagination, useRowSelect,
    hooks => {
      hooks.visibleColumns.push(columns => [
        {
          id: 'selection',
          Header: ({ getToggleAllPageRowsSelectedProps }) => (
            <div>
              <IndeterminateCheckbox {...getToggleAllPageRowsSelectedProps()} />
            </div>
          ),
          Cell: ({ row }) => (
            <div>
              <IndeterminateCheckbox {...row.getToggleRowSelectedProps()} />
            </div>
          ),
        },
        ...columns,
      ])
    } )

    const { pageIndex , pageSize, globalFilter } = state;

    useEffect(() => {
      localStorage.removeItem('profileStatus');
      localStorage.removeItem('ownerName');
    }, []);
    let profileStatusVal = localStorage.getItem('profileStatus');
  return (
    <div>
      <Header globalFilter={globalFilter} setGlobalFilter={setGlobalFilter} data={data} />
      <div className='App-body'>
        <div className='container-fluid'>
          <div className='App-body-container'>
            <div className='App-body-subHeader'>
              <div>{profileStatusVal === 'not_verified' ? 'Not Verified Records' : profileStatusVal === 'to_verify' ? 'To Verify Records' : profileStatusVal === 'rejected' ? 'Rejected Records' : 'Verified Records' }</div>
              <div className='wrapper-data'>
                <ModalDialog />
                <Select options={tenant} changeFun={showDataIfTM} />
              </div>
            </div>
            <table {...getTableProps()}>
              <thead>
                {headerGroups.map((headerGroup) => (
                  <tr {...headerGroup.getHeaderGroupProps()}>
                    {headerGroup.headers.map((column) => {
                      return (
                        <th {...column.getHeaderProps()}>
                        {column.render("Header")}
                        {/* Render the columns filter UI */}
                        <div>{column.canFilter ? column.render("Filter") : null}</div>
                      </th>
                      )
                      })}
                  </tr>
                ))}
              </thead>

              <tbody {...getTableBodyProps()}>
                {page.map((row) => {
                  prepareRow(row)
                  return (
                    <tr {...row.getRowProps()}>
                      {row.cells.map((cell) => (
                        <td {...cell.getCellProps()}>{cell.render("Cell")}</td>
                      ))}
                    </tr>
                  )}
                )}
              </tbody>
            </table>
            <div className='pagination'>
              <span>
              Page: {''}
              <input type='number' defaultValue={pageIndex + 1}
                  onChange={e => {
                    const pageNumber =  e.target.value ? Number(e.target.value) - 1 : 0
                    gotoPage(pageNumber)
                  }}
                  style={{width: '30px'}}
                />
              </span>
              <div className='rowsPerPage'>
                <span>Rows per page:</span>
                <Form.Select value={pageSize} onChange={e => setPageSize(Number(e.target.value))}>
                    {
                      [10,25, 50].map(pageSize => (
                        <option key={pageSize} value={pageSize}>
                          {pageSize}
                        </option>
                      ))
                    }
                </Form.Select>
              </div>        
              <span  className='ms-4'>
              {pageIndex + 1} of {pageOptions.length}
              </span>  
            <div className='ms-4'>
              <button onClick={() => previousPage()} disabled={!canPreviousPage}><Icon.ChevronLeft /></button>
              <button onClick={() => nextPage()} disabled={!canNextPage}><Icon.ChevronRight /></button>
            </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default PartnerManagementDashboard1;
