import React , {useState} from 'react';
import FormGroup from '@mui/material/FormGroup';
import FormControlLabel from '@mui/material/FormControlLabel';
import Checkbox from '@mui/material/Checkbox';
import Select from './SelectComponent';

function OwnerAssignmentData() {
    const getInitialState = () => {
        const value = '';
        return value;
    };
    const [value, setValue] = useState(getInitialState);
    const [option, setOption] = useState('');
    const [ownerName, setOwnerName] = useState([]);

    const handleChange = (e) => {
        setValue(e.target.value);
    };

    const onSelectingOption = (e) => {
        setOption(e.target.value)
    };

    const onOwnerNameSelection = (e) => {
        setOwnerName(e.target.value);
    };

    var assignment = [
        {
            displayName: 'Select to Assign Owner',
            value: '',
        },
        {
            displayName: "Verification Owner",
            value: "verification_owner",
        },
        {
            displayName: "QC Owner",
            value: "qc_owner"
        }
    ];

    var Owners = [
        {
            id: 1,
            name: "Ankita Sangodkar",
            value: "ankita",
        }
    ];

  return (
      <div>
        <Select value={value} options={assignment} changeFun={handleChange} />
        <FormGroup>
            {value === 'verification_owner' || value === 'qc_owner' ? (
                <span>
                <FormControl>
                    <RadioGroup>
                        <FormControlLabel
                            value="assign_owner" 
                            label="Assign Owner" 
                            name="assignment" 
                            control={<Radio  />} 
                            id="assign_owner"
                            onChange={onSelectingOption}
                        />
                        {option === 'assign_owner' ? (
                            <div>
                                {Owners.map(owner => (
                                    <FormControlLabel 
                                        key={owner.id}
                                        value={owner.name}
                                        control={<Checkbox />} 
                                        label={owner.name}
                                        className="ms-4"
                                        onChange={onOwnerNameSelection}
                                    />
                                ))}
                            </div>
                        ): null }
                        <FormControlLabel
                            value="remove_owner"
                            label="Remove Owner"
                            name="assignment" 
                            control={<Radio  />} 
                            id="remove_owner"
                            onChange={onSelectingOption}
                        />
                    </RadioGroup>
                </FormControl>
                </span>
            ) : (null)}
        </FormGroup>
    </div>
  );
}

export default OwnerAssignmentData;
