import React , {useState} from 'react';
import Form from 'react-bootstrap/Form';
import Select from './SelectComponent';

function OwnerAssignmentData() {
    const getInitialState = () => {
        const value = '';
        return value;
    };
    const [value, setValue] = useState(getInitialState);
    const [option, setOption] = useState('');
    const [ownerName, setOwnerName] = useState([]);

    const handleChange = (e) => {
        setValue(e.target.value);
    };

    const onSelectingOption = (e) => {
        setOption(e.target.value)
    };

    const onOwnerNameSelection = (e) => {
        setOwnerName(e.target.value);
    };

    var assignment = [
        {
            displayName: 'Select to Assign Owner',
            value: '',
        },
        {
            displayName: "Verification Owner",
            value: "verification_owner",
        },
        {
            displayName: "QC Owner",
            value: "qc_owner"
        }
    ];

    var Owners = [
        {
            id: 1,
            name: "Ankita Sangodkar",
            value: "ankita",
        }
    ];

  return (
      <div>
        <Select value={value} options={assignment} changeFun={handleChange} />
        <Form.Group controlId="kindOfStand">
            {value === 'verification_owner' || value === 'qc_owner' ? (
                <span>
                    <Form>
                        <Form.Check
                            value="assign_owner"
                            label="Assign Owner"
                            name="assignment"
                            type="radio"
                            id="assign_owner"
                            onChange={onSelectingOption}
                        />
                        {option === 'assign_owner' ? (
                            <div>
                                {Owners.map(owner => (
                                    <Form.Check
                                        key={owner.id}
                                        value={owner.name}
                                        type="checkbox"
                                        label={owner.name}
                                        className="ms-4"
                                        onChange={onOwnerNameSelection}
                                    />
                                ))}
                            </div>
                        ): null }
                        <Form.Check
                            value="remove_owner"
                            label="Remove Owner"
                            name="assignment"
                            type="radio"
                            id="remove_owner"
                            onChange={onSelectingOption}
                        />
                    </Form>
                </span>
            ) : (null)}
        </Form.Group>
    </div>
  );
}

export default OwnerAssignmentData;
